object_address_id.newRowSelectOptions:
{{
// Берем два столбца из исходных данных, удаляем дубликаты и сортируем по label
lsfusion_obj.data
  .filter(function(item, index, self) {
    // Удаляем дубликаты на основе "Адрес объекта" и "key0 Объекта"
    return (
      index ===
      self.findIndex(function(other) {
        return other["Адрес объекта"] === item["Адрес объекта"] && other["key0 Объекта"] === item["key0 Объекта"];
      })
    );
  })
  .map(function(item) {
    // Преобразуем данные в формат { label, value }
    return {
      label: item["Адрес объекта"], // Используем адрес как label
      value: item["key0 Объекта"]   // Используем key0 Объекта как value
    };
  })
  .sort(function(a, b) {
    // Сортируем по label (алфавитный порядок)
    return a.label.localeCompare(b.label);
  })
}}

